mapper.select:
SELECT anthology._id AS _id, anthology.title AS title, anthology.summary AS summary, anthology.create_date AS create_date, anthology.update_date AS update_date, anthology.publish_date AS publish_data, anthology.cover_image_id AS cover_image_id, author._id AS author_id, authentication.nick_name AS author_nick_name, anthology.additional_info_id AS additional_info_id FROM author author INNER JOIN anthology anthology ON author._id = anthology.author_id INNER JOIN authentication_author authenticationAndAuthor ON author._id = authenticationAndAuthor.author_id INNER JOIN authentication authentication ON authenticationAndAuthor.authentication_id = authentication._id ORDER BY anthology.publish_date
DESC
LIMIT #{start}, #{pageSize}

mapper.insert:
INSERT INTO anthology_additional_info (view_number, praise_number, comment_number, bookmark_number, followup_number)
VALUES (
    #{viewNumber}, #{praiseNumber}, #{commentNumber}, #{bookmarkNumber},
    #{followupNumber}
);

mapper.update:
UPDATE anthology_additional_info
SET view_number     = #{viewNumber}, praise_number = #{praiseNumber},
    comment_number  = #{commentNumber},
    bookmark_number = #{bookmarkNumber},
    followup_number = #{followupNumber}
WHERE _id = #{id}
