mapper.select:
SELECT article._id AS _id, article.title AS title, article.summary AS summary, author._id AS author_id, authentication.nick_name AS author_nick_name, author.icon_image_id AS author_icon_image_id, article.publish_date AS publish_date, anthology._id AS anthology_id, anthology.title AS anthology_title, article.additional_info_id AS additional_info_id, article.cover_image_id AS cover_image_id FROM author author INNER JOIN anthology anthology ON author._id = anthology.author_id INNER JOIN article article ON anthology._id = article.anthology_id INNER JOIN authentication_author authenticationAndAuthor ON author._id = authenticationAndAuthor.author_id INNER JOIN authentication authentication ON authenticationAndAuthor.authentication_id = authentication._id ORDER BY article.publish_date
DESC
LIMIT #{start}, #{pageSize}

mapper.insert:
INSERT INTO article (title, summary, content, create_date, update_date, publish_date, anthology_id, additional_info_id, cover_image_id)
VALUES (
    #{title}, #{summary}, #{content}, #{createDate}, #{updateDate},
    #{publishDate},
    #{anthologyId},
    #{additionalInfoId},
    #{coverImageId}
);

INSERT INTO article_additional_info (view_number, praise_number, comment_number, bookmark_number)
VALUES (
    #{viewNumber}, #{praiseNumber}, #{commentNumber}, #{bookmarkNumber}
);

mapper.update:
UPDATE article_additional_info
SET view_number     = #{viewNumber}, praise_number = #{praiseNumber},
    comment_number  = #{commentNumber},
    bookmark_number = #{bookmarkNumber}
WHERE _id = #{id}
